def isodd(number):
	io = remote("47.96.239.28","23333")
	io.recvuntil("now\n")
	send_string = hex(number)[2:].strip("L")
	io.sendline(send_string)
	info = io.recvline()
	if info == "odd\n":
		io.close()
		return True
	else:
		io.close()
		return False
